import React, { useState } from 'react';

const RegistrationPage = () => {
  const [userType, setUserType] = useState('user'); // default to 'user'

  const handleUserTypeChange = (event) => {
    setUserType(event.target.value);
  };

  return (
    <div>
      <h2>Register as:</h2>
      <select onChange={handleUserTypeChange} value={userType}>
        <option value="user">User</option>
        <option value="admin">Administrator</option>
      </select>

      {userType === 'user' ? (
        <UserRegistrationForm />
      ) : (
        <AdminRegistrationForm />
      )}
    </div>
  );
};

const UserRegistrationForm = () => {
  return (
    <form>
      {/* User registration fields */}
      <h3>User Registration</h3>
      {/* Add your form fields here */}
      <button type="submit">Register as User</button>
    </form>
  );
};

const AdminRegistrationForm = () => {
  return (
    <form>
      {/* Administrator registration fields */}
      <h3>Administrator Registration</h3>
      {/* Add your form fields here */}
      <button type="submit">Register as Administrator</button>
    </form>
  );
};

export default RegistrationPage;
