import React, { useState } from 'react';

const PersonalPage = () => {
  const [userData, setUserData] = useState({
    name: '',
    phone: '',
    specialty: '',
    email: '',
    password: ''
  });
  const [reservations, setReservations] = useState([]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const handleChangePassword = (newPassword) => {
    setUserData({ ...userData, password: newPassword });
  };

  const handleCancelReservation = (reservationId) => {
    // Logic to cancel reservation
  };

  return (
    <div className="container mx-auto p-4">
      <div className="mb-4">
        <input
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          type="text"
          name="name"
          placeholder="Name"
          value={userData.name}
          onChange={handleInputChange}
        />
      </div>
      {/* Repeat for other fields... */}
      <div className="mb-4">
        <input
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          type="tel"
          name="phone"
          placeholder="Phone Number"
          value={userData.phone}
          onChange={handleInputChange}
        />
      </div>
      <div className="mb-4">
        <input
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          type="text"
          name="specialty"
          placeholder="Specialty"
          value={userData.specialty}
          onChange={handleInputChange}
        />
      </div>
      <div className="mb-4">
        <input
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          type="email"
          name="email"
          placeholder="Email"
          value={userData.email}
          onChange={handleInputChange}
        />
      </div>
      <div className="mb-4">
        <input
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          type="password"
          placeholder="Password"
          value={userData.password}
          onChange={(e) => handleChangePassword(e.target.value)}
        />
      </div>

      <h2 className="text-2xl font-bold mt-8 mb-4">Reservations</h2>
      <div className="space-y-4">
        {reservations.length > 0 ? (
          reservations.map((reservation) => (
            <div key={reservation.id} className="flex justify-between items-center p-4 bg-gray-100 rounded">
              <span className="font-medium">{reservation.eventName}</span>
              <button
                className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                onClick={() => handleCancelReservation(reservation.id)}
              >
                Cancel Reservation
              </button>
            </div>
          ))
        ) : (
          <p>No reservations have been made.</p>
        )}
      </div>
    </div>
  );
};

export default PersonalPage;
