import React, { useState } from 'react';
import { Link } from "react-router-dom";
import Logo from './img/Logo.png';

const TeamMember = ({ name, jobTitle, specialization, imageUrl }) => {
  return (
    <div className="flex flex-col items-center space-y-2">
      <img src={imageUrl} alt={name} className="rounded-full w-24 h-24" />
      <div className="text-center">
        <h3 className="text-lg font-semibold">{name}</h3>
        <p className="text-sm">{jobTitle}</p>
        <p className="text-sm">{specialization}</p>
      </div>
    </div>
  );
};

const Create = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const teamMembers = [
    {
      name: 'John Doe',
      jobTitle: 'Web Developer',
      specialization: 'Frontend',
      imageUrl: 'Logo.png',
    },
    // Add more team members as needed
  ];

  const chunkedTeamMembers = teamMembers.reduce((result, item, index) => {
    const chunkIndex = Math.floor(index / 3);

    if (!result[chunkIndex]) {
      result[chunkIndex] = [];
    }

    result[chunkIndex].push(item);

    return result;
  }, []);

  return (
    <>
      <nav className="bg-purple-900 md:flex flex-col md:flex-row md:space-x-2 p-4 space-x-0 md:space-y-0 fixed w-full top-0 z-10">
        {/* Navigation content */}
      </nav>

      <div className="flex flex-col items-center space-y-2 mt-24">
        <div className="p-28 pb-28">
          <h1 className="text-3xl font-bold tracking-tighter sm:text-5xl pb-4"> Team members</h1>
          <div className="flex flex-col items-center space-y-2 pt-60">
            {/* Render team members in rows */}
            {chunkedTeamMembers.map((chunk, index) => (
              <div key={index} className="flex space-x-4">
                {chunk.map((member, i) => (
                  <TeamMember key={i} {...member} />
                ))}
              </div>
            ))}
          </div>
        </div>
        {/* Other sections */}
      </div>
    </>
  );
};

export default Create;
