import React, { useState } from 'react';

const EventInvitation = () => {
  const [invitation, setInvitation] = useState({
    recipientName: '',
    recipientEmail: '',
    eventTitle: '',
    eventDescription: '',
    eventDate: '',
    eventTime: '',
    message: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInvitation(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle the invitation sending logic here
    console.log('Invitation Details:', invitation);
    alert('Invitation sent successfully!');
  };

  return (
    <div className="container mx-auto mt-10">
      <h2 className="text-center text-2xl font-bold mb-6">Send Event Invitation</h2>
      <form onSubmit={handleSubmit} className="max-w-md mx-auto">
        {/* Recipient's Name */}
        <div className="mb-4">
          <label htmlFor="recipientName" className="block text-gray-700 text-sm font-bold mb-2">
            Recipient's Name:
          </label>
          <input
            type="text"
            id="recipientName"
            name="recipientName"
            value={invitation.recipientName}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            required
          />
        </div>
        {/* Recipient's Email */}
        <div className="mb-4">
          <label htmlFor="recipientEmail" className="block text-gray-700 text-sm font-bold mb-2">
            Recipient's Email:
          </label>
          <input
            type="email"
            id="recipientEmail"
            name="recipientEmail"
            value={invitation.recipientEmail}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            required
          />
        </div>
        {/* Event Title */}
        <div className="mb-4">
          <label htmlFor="eventTitle" className="block text-gray-700 text-sm font-bold mb-2">
            Event Title:
          </label>
          <input
            type="text"
            id="eventTitle"
            name="eventTitle"
            value={invitation.eventTitle}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            required
          />
        </div>
        {/* Event Description */}
        <div className="mb-4">
          <label htmlFor="eventDescription" className="block text-gray-700 text-sm font-bold mb-2">
            Event Description:
          </label>
          <textarea
            id="eventDescription"
            name="eventDescription"
            value={invitation.eventDescription}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            required
          />
        </div>
        {/* Event Date */}
        <div className="mb-4">
          <label htmlFor="eventDate" className="block text-gray-700 text-sm font-bold mb-2">
            Event Date:
          </label>
          <input
            type="date"
            id="eventDate"
            name="eventDate"
            value={invitation.eventDate}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            required
          />
        </div>
        {/* Event Time */}
        <div className="mb-4">
          <label htmlFor="eventTime" className="block text-gray-700 text-sm font-bold mb-2">
            Event Time:
          </label>
          <input
            type="time"
            id="eventTime"
            name="eventTime"
            value={invitation.eventTime}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            required
          />
        </div>
        {/* Personal Message */}
        <div className="mb-6">
          <label htmlFor="message" className="block text-gray-700 text-sm font-bold mb-2">
            Personal Message:
          </label>
          <textarea
            id="message"
            name="message"
            value={invitation.message}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            required
          />
        </div>
        {/* Submit Button */}
        <div className="flex items-center justify-between">
          <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
            Send Invitation
          </button>
        </div>
      </form>
    </div>
  );
};

export default EventInvitation;
