import React, { useState } from "react";

const ProfileCard = ({ name, nationalId, college, academicLevel, email }) => {
  const [editMode, setEditMode] = useState(false);

  const handleEdit = () => {
    setEditMode(true);
  };

  const handleSave = () => {
    // Save the edited profile information
    setEditMode(false);
  };

  return (
    
    <div className="max-w-sm bg-white border border-gray-200 rounded-lg shadow p-5">
      <div className="flex items-center justify-between">
        <div>
          <h5 className="text-2xl font-bold text-gray-900">{name}</h5>
          <p className="text-gray-700">{college}</p>
          <p className="text-gray-700">{academicLevel}</p>
        </div>
        {!editMode && (
          <button
            onClick={handleEdit}
            className="bg-blue-700 hover:bg-green-600 text-white font-medium py-2 px-3 rounded-lg"
          >
            Edit
          </button>
        )}
      </div>
      <div className="mt-5">
        <p className="text-gray-500 font-bold">National ID: {nationalId}</p>
        <p className="text-gray-500 font-bold">Email: {email}</p>
      </div>
      {editMode && (
        <div className="mt-5">
          {/* Input fields for editing */}
          <input
            type="text"
            placeholder="Name"
            className="border border-gray-300 rounded px-2 py-1"
          />
          <input
            type="text"
            placeholder="National ID"
            className="border border-gray-300 rounded px-2 py-1"
          />
          <input
            type="text"
            placeholder="College"
            className="border border-gray-300 rounded px-2 py-1"
          />
          <input
            type="text"
            placeholder="Academic Level"
            className="border border-gray-300 rounded px-2 py-1"
          />
          <input
            type="email"
            placeholder="Email"
            className="border border-gray-300 rounded px-2 py-1"
          />
          <div className="flex justify-end mt-2">
            <button
              onClick={handleSave}
              className="bg-green-500 hover:bg-green-600 text-white font-medium py-1 px-2 rounded"
            >
              Save
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProfileCard;
