import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import myPhoto from './img/3.jpg'; // Import your photo file
import Logo from './img/Logo.png';

const LoginPage = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (formData.email === 'Evendango@gmail.com' && formData.password === '123456') {
      window.location.href = '/home';
    } else {
      setError('Email or password is incorrect');
    }
  };

  return (
    <>
     <nav className=" md:flex  md:flex-row md:space-x-2 p-4 space-x-0 md:space-y-0 fixed w-full top-0 z-10  bg-gay-200">
        <div className="container mx-auto flex flex-col md:flex-row md:justify-between md:items-center">
          <div className="flex justify-between items-center w-full md:w-auto ">
          <img className="object-cover " src={Logo} alt="Profile" />
          
          </div>
        </div>
      </nav>
     
    
    </>
  );
};

export default LoginPage;
