import React from 'react';

const LoginPage = () => {
  return (
    <div className="min-h-screen flex flex-col md:flex-row items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      {/* Photo container */}
      <div className="md:w-1/2 flex justify-center">
        <img src="/path-to-your-image.jpg" alt="Login Visual" className="rounded-lg shadow-lg" />
      </div>

      {/* Login form container */}
      <div className="max-w-md w-full space-y-8 md:w-1/2">
        <div>
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
            Welcome Back!
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600">
            Login to continue
          </p>
        </div>
        <form className="mt-8 space-y-6" action="#" method="POST">
          {/* ... rest of the form ... */}
        </form>
      </div>
    </div>
  );
}

export default LoginPage;
