import React, { useState } from 'react';
const EventTypeCard = ({ title, description, icon }) => {
  return (
    <div className="border p-4 rounded-lg">
      <div className="flex items-center mb-4">
        <span className="text-2xl mr-4">{icon}</span>
        <h3 className="text-lg font-semibold">{title}</h3>
      </div>
      <p>{description}</p>
      <div className="mt-4">
        <input type="radio" name="event-type" id={title} />
        <label htmlFor={title} className="ml-2">{title}</label>
      </div>
    </div>
  );
};

const EventForm = ({ onAddEvent }) => {
  const [id, setId] = useState('');
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(null); 
  const [date, setDate] = useState('');
  const [hour, setHour] = useState('');
  const [address, setAddress] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    const newEvent = {
      id,
      title,
      description,
      imageUrl: URL.createObjectURL(image), 
      date,
      hour,
      address,
    };
    onAddEvent(newEvent);
    setId('');
    setTitle('');
    setDescription('');
    setImage(null);
    setDate('');
    setHour('');
    setAddress('');
  };

  return (
    <>
    <nav className="bg-gray-800 py-4">
      <div className="container mx-auto flex justify-between items-center">
        <div className="text-white font-bold ">Even<span className="text-yellow-500">D</span>ango</div>
        <ul className="flex space-x-4">
          <li><a href="/home" className="text-white  hover:text-yellow-500">Home</a></li>
          <li><a href="/event" className="text-white  hover:text-yellow-500">Create</a></li>
          <li><a href="/invit" className="text-white  hover:text-yellow-500">Invitations</a></li>
          <li><a href="/home" className="text-white  hover:text-yellow-500">Profile</a></li>
          <li><a href="/about" className="text-white  hover:text-yellow-500">About</a></li>
          <li><a href="/contact" className="text-white  hover:text-yellow-500">Contact</a></li>
        </ul>
      </div>
    </nav>
   
    <div className="bg-white p-8">
      <div className="flex justify-between items-center border-b pb-4">
        <h1 className="text-xl font-semibold">Create Event</h1>
        <div>
          <button className="text-blue-500 hover:text-blue-700">Cancel</button>
          <button className="ml-4 bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-700">Create Draft</button>
        </div>
      </div>
      <p className="text-gray-600 mt-4">
        First, fill out basic event info. After this step, you can set up video conferencing, tickets, registration and much more.
      </p>
      <div className="mt-8">
        <h2 className="text-lg font-semibold mb-4">Event type</h2>
        <div className="flex gap-4">
          <EventTypeCard
            title="Ticketed event"
            description="Offer paid or free tickets, set currency & tax. Perfect for classes, workshops, conferences."
            icon="ðŸŽŸï¸" // Replace with actual icon
          />
          <EventTypeCard
            title="RSVP event"
            description="Collect RSVPs, set a waitlist. Perfect for weddings, private events, parties."
            icon="âœ‰ï¸" // Replace with actual icon
          />
        </div>
      </div>
    </div>
    </>
  );
};
const EventDateTimePicker = () => {
  const [eventDetails, setEventDetails] = useState({
    eventType: 'single', // 'single', 'recurring', 'tbd'
    startDate: '',
    startTime: '',
    endDate: '',
    endTime: '',
    timezone: 'GMT+02:00',
    displayEndDate: true,
    displayTimezone: true
  });

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setEventDetails({
      ...eventDetails,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  return (
    <div className="bg-white p-8">
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Event Type
        </label>
        <select
          name="eventType"
          value={eventDetails.eventType}
          onChange={handleInputChange}
          className="shadow border rounded py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
        >
          <option value="single">Single event</option>
          <option value="recurring">Recurring event</option>
          <option value="tbd">To be decided (TBD)</option>
        </select>
      </div>
      {/* Add date and time inputs here */}
      {/* Add timezone selection here */}
      {/* Add display options here */}
      {/* The rest of your form elements */}
    </div>
  );
};
export default EventForm;
