import React, { useState } from 'react';
import { Link } from "react-router-dom";

const teamMembers = [
  {
    id: 1,
    name: "Youssef Mohamed",
    role: "CTO",
    photo: "/team/Y.jpg",
    description: "Back-End",
    social: {
      facebook: "https://facebook.com/flepoooo",
      linkedin: "https://www.linkedin.com/in/flepooo",
      github: "https://github.com/flepooo",
    },
  },
  {
    id: 2,
    name: "Eslam Eid",
    role: "CEO",
    photo: "/team/E.jpg",
    description: "Head of Front-End",
    social: {
      facebook: "https://facebook.com/flepoooo",
      linkedin: "https://www.linkedin.com/in/flepooo",
      github: "https://github.com/flepooo",
    },
  },
  {
    id: 3,
    name: "Basem Mohamed",
    role: "Dev",
    photo: "/team/B.jpeg",
    description: "Front-End",
    social: {
      facebook: "https://facebook.com/flepoooo",
      linkedin: "https://www.linkedin.com/in/flepooo",
      github: "https://github.com/flepooo",
    },
  },
  {
    id: 4,
    name: "Ahmed Salah",
    role: "Dev",
    photo: "/team/A.jpeg",
    description: "Front-End",
    social: {
      facebook: "https://facebook.com/flepoooo",
      linkedin: "https://www.linkedin.com/in/flepooo",
      github: "https://github.com/flepooo",
    },
  },
  // Add more team members here
];

const About = () => {
  return (
    <main className="p-4 md:p-10 mx-auto max-w-7xl">
      <div className="container mx-auto py-8">
        <h1 className="text-4xl font-bold mb-4">About Us</h1>
        <p className="text-lg mb-8">
          Welcome to our company! We are a team of passionate individuals
          dedicated to making the world a better place through technology.
        </p>
        <h2 className="text-2xl font-bold mb-4">Our Team</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4">
          {teamMembers.map((member) => (
            <div key={member.id} className="border p-4 rounded-lg">
              <div className="flex justify-center">
                <img
                  src={member.photo}
                  alt={member.name}
                  className="rounded-full"
                />
              </div>
              <div className="text-center mt-4">
                <h3 className="text-xl font-bold">{member.name}</h3>
                <p className="text-gray-600 mb-2">{member.role}</p>
                <p>{member.description}</p>
                <div className="flex justify-center mt-4">
                  <a
                    href={member.social.github}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="mr-4"
                  >
                    GitHub
                  </a>
                  <a
                    href={member.social.linkedin}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="mr-4"
                  >
                    LinkedIn
                  </a>
                  <a
                    href={member.social.facebook}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    Facebook
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
      <p className="text-lg font-medium my-3">
        © Copyright Mathematics Dept. All Rights Reserved.
      </p>
      <p>Designed by DevelopmentTeam</p>
    </main>
  );
};

export default About;
