import React, { useState } from 'react';

const PersonalPage = () => {
  const [userData, setUserData] = useState({
    name: 'John Doe',
    phone: '123-456-7890',
    specialty: 'Software Engineering',
    email: 'john.doe@example.com',
    password: 'password123' // This should be handled more securely in a real application
  });
  const [reservations, setReservations] = useState([
    { id: 1, eventName: 'Tech Conference', booked: true },
    { id: 2, eventName: 'Music Festival', booked: false }
  ]);

  const handleCancelReservation = (reservationId, password) => {
    if (password === userData.password) {
      setReservations(reservations.map(reservation => {
        if (reservation.id === reservationId) {
          return { ...reservation, booked: false };
        }
        return reservation;
      }));
    } else {
      alert('Incorrect password');
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Personal Information</h2>
      <div className="mb-4">
        <p className="shadow border rounded w-full py-2 px-3 text-gray-700">
          Name: {userData.name}
        </p>
      </div>
      <div className="mb-4">
        <p className="shadow border rounded w-full py-2 px-3 text-gray-700">
          Phone: {userData.phone}
        </p>
      </div>
      <div className="mb-4">
        <p className="shadow border rounded w-full py-2 px-3 text-gray-700">
          Specialty: {userData.specialty}
        </p>
      </div>
      <div className="mb-4">
        <p className="shadow border rounded w-full py-2 px-3 text-gray-700">
          Email: {userData.email}
        </p>
      </div>

      <h2 className="text-2xl font-bold mt-8 mb-4">Reservations</h2>
      <div className="space-y-4">
        {reservations.length > 0 ? (
          reservations.map((reservation) => (
            <div key={reservation.id} className="flex justify-between items-center p-4 bg-gray-100 rounded">
              <span className="font-medium">{reservation.eventName}</span>
              {reservation.booked ? (
                <button
                  className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  onClick={() => handleCancelReservation(reservation.id, userData.password)}
                >
                  Cancel Reservation
                </button>
              ) : (
                <span>No reservations</span>
              )}
            </div>
          ))
        ) : (
          <p>No reservations have been made.</p>
        )}
      </div>
    </div>
  );
};

export default PersonalPage;
