const Card = ({ id, title, description, imageUrl, date, hour, address }) => {
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [password, setPassword] = useState("");
  const [bookingConfirmed, setBookingConfirmed] = useState(false);

  const handleConfirm = () => {
    if (password === "123456") {
      console.log("Booking confirmed with password:", password);
      setBookingConfirmed(true);
      setShowConfirmation(false);
    } else {
      console.log("Incorrect password. Booking not confirmed.");
      // You can also display an error message here if you want
    }
  };

  return (
    <div className="max-w-sm bg-white border border-gray-200 rounded-lg shadow">
      <a href="#">
