          <h5 className="mb-2 text-2xl font-bold tracking-tight text-gray-900">{title}</h5>
        </a>
        <p className="mb-3 font-normal text-gray-700">{description}</p>
        <div className="flex justify-between items-center">
          {!bookingConfirmed && (
            <button
              onClick={() => setShowConfirmation(true)}
              className="bg-blue-700 hover:bg-blue-800 text-white font-medium py-2 px-3 rounded-lg"
            >
              Book
            </button>
          )}
          {showConfirmation && (
            <div className="absolute inset-0 flex items-center justify-center bg-gray-800 bg-opacity-90 p-5 rounded-lg">
              <div className="bg-white p-4 rounded-lg">
                <p className="mb-2">Enter your password to confirm booking:</p>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="border border-gray-300 rounded px-2 py-1"
                />
                <div className="mt-2 flex justify-end">
                  <button
                    onClick={handleConfirm}
                    className="bg-green-500 hover:bg-green-600 text-white font-medium py-1 px-2 rounded"
                  >
                    Confirm
                  </button>
                  <button
                    onClick={() => setShowConfirmation(false)}
                    className="ml-2 bg-red-500 hover:bg-red-600 text-white font-medium py-1 px-2 rounded"
                  >
                    Cancel
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
        <p className="text-gray-500 text-sm mt-2 font-bold">Date: {date}</p>
        <p className="text-gray-500 text-sm font-bold">Hour: {hour}</p>
        <p className="text-gray-500 text-sm font-bold">Address: {address}</p>
      </div>
    </div>
  );
};

