import React, { useState } from "react";
import * as faceapi from "face-api.js";

const BookingForm = () => {
  const [isRecognized, setIsRecognized] = useState(false);

  const startFacialRecognition = async () => {
    await faceapi.nets.tinyFaceDetector.loadFromUri("/models");
    await faceapi.nets.faceLandmark68Net.loadFromUri("/models");
    await faceapi.nets.faceRecognitionNet.loadFromUri("/models");

    const video = document.createElement("video");
    document.body.appendChild(video);

    const stream = await navigator.mediaDevices.getUserMedia({ video: {} });
    video.srcObject = stream;
    await video.play();

    const canvas = faceapi.createCanvasFromMedia(video);
    document.body.appendChild(canvas);

    const displaySize = { width: video.width, height: video.height };
    faceapi.matchDimensions(canvas, displaySize);

    setInterval(async () => {
      const detections = await faceapi.detectAllFaces(video, new faceapi.TinyFaceDetectorOptions()).withFaceLandmarks().withFaceDescriptors();
      const resizedDetections = faceapi.resizeResults(detections, displaySize);
      canvas.getContext("2d").clearRect(0, 0, canvas.width, canvas.height);
      faceapi.draw.drawDetections(canvas, resizedDetections);
      faceapi.draw.drawFaceLandmarks(canvas, resizedDetections);
      const results = resizedDetections.map(d => faceapi.matchDimensions(canvas, displaySize));
    }, 100);
  };

  const handleBooking = () => {
    if (isRecognized) {
      // Proceed with the booking process
      alert("Booking confirmed!");
    } else {
      alert("Please complete facial recognition to proceed with the booking.");
    }
  };

  return (
    <div>
      <h2>Booking Form</h2>
      <button onClick={startFacialRecognition}>Start Facial Recognition</button>
      <button onClick={handleBooking}>Book</button>
    </div>
  );
};

const Card = ({ id, title, description, imageUrl, date, hour, address }) => {
  const [showConfirmation, setShowConfirmation] = React.useState(false);

  const handleBookClick = () => {
    setShowConfirmation(true);
  };

  const handleConfirm = () => {
    setShowConfirmation(false);
  };

  return (
    <div className="group relative bg-gray-200 bg-opacity-75 shadow-lg rounded-lg overflow-hidden">
      <div className="relative">
        <img
          className="h-96 w-full object-cover transition-transform transform group-hover:scale-105"
          src={imageUrl}
          alt={title}
        />
        <div className="absolute inset-0 bg-gray-800 bg-opacity-90 flex flex-col justify-center items-center opacity-0 group-hover:opacity-100 transition-opacity">
          <div className="text-white px-6 py-4 m-8">
            <p className="text-yellow-600 text-base font-bold m-4">{description}</p>
            <p className="text-yellow-500 text-sm mt-2 font-bold m-4">Date: {date}</p>
            <p className="text-yellow-500 text-sm font-bold m-4">Hour: {hour}</p>
            <p className="text-yellow-500 text-sm font-bold m-4">Address: {address}</p>
            <button
              onClick={handleBookClick}
              className="mt-4 bg-gray-800 hover:bg-green-400 text-white font-bold py-2 px-4 rounded m-4"
            >
              Book
            </button>
          </div>
        </div>
      </div>
      <div className="absolute top-0 left-0 w-full bg-gray-800 bg-opacity-90 px-6 py-4 text-white font-extrabold text-xl">
        {title}
      </div>
      {showConfirmation && (
        <div className="absolute inset-0 flex items-center justify-center bg-gray-800 bg-opacity-90">
          <div className="bg-white p-8 rounded shadow-lg">
            <h2 className="text-2xl font-bold">Booking Confirmation</h2>
            <p className="text-lg">Please confirm your booking by entering your password:</p>
            <input
              type="password"
              placeholder="Enter your password"
              className="border border-gray-300 rounded px-4 py-2 mt-4"
            />
            <button
              onClick={handleConfirm}
              className="bg-gray-800 hover:bg-green-400 text-white font-bold py-2 px-4 rounded mt-4"
            >
              Confirm
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

const Home = () => {
  return (
    <>
      <nav className="bg-gray-800 py-4">
        <div className="container mx-auto flex justify-between items-center">
          <div className="text-white font-bold ">Even<span className="text-yellow-500">D</span>ango</div>
          <ul className="flex space-x-4">
            <li><a href="/home" className="text-white  hover:text-yellow-500">Home</a></li>
            <li><a href="/event" className="text-white  hover:text-yellow-500">Creat</a></li>
            <li><a href="/invit" className="text-white  hover:text-yellow-500">Invitations</a></li>
            <li><a href="/home" className="text-white  hover:text-yellow-500">profile</a></li>
            <li><a href="/about" className="text-white  hover:text-yellow-500">About</a></li>
            <li><a href="#" className="text-white  hover:text-yellow-500">Contact</a></li>
          </ul>
        </div>
      </nav>
      <div className="container mx-auto w-2/3  bg-gray-100">
        <div className="flex flex-col mt-8 ">
          <Card
            id={"1"}
            title="College of Science Conference"
            description="تتشرف وحدة الدعم الطلابي بكلية العلوم - جامعة قناة السويس -
          بدعوة سيادتكم لحضور فعاليات مؤتمر كلية العلوم السنوي السابع للأبحاث الطلابية ٢٠٢٤
           تحت عنوان :  
          «التحديات والإبداع: دور الذكاء الاصطناعي في تطوير العلوم»"
            imageUrl={require("./img/399994743_786024586870490_5833074945972968829_n-1.jpg")}
            date={"17/4/2024"}
            hour={"10: AM"}
            address={"Faculty of Science, Hall 1, Library Building - Ismailia"}
          />
        </div>
        <div className="flex flex-col mt-8 ">
          <Card
            id={"2"}
            title="Department heads meeting"
            description="The Student Support Unit at the Faculty of Science - Suez Canal University is honored -
          Inviting you to attend the activities of the College of Science’s seventh annual student research conference 2024
            Under the title :
          “Challenges and Creativity: The Role of Artificial Intelligence in Developing Science”."
            imageUrl={require("./img/1.jpg")}
            date={"17/4/2024"}
            hour={"10: AM"}
            address={"Faculty of Science, Hall 1, Library Building - Ismailia"}
          />
        </div>
      </div>
    </>
  );
};

export default Home;
