import React, { useState } from 'react';
import NavbarPage from './Home/navbar/navbar';
const PersonalPage = () => {
  const [userData, setUserData] = useState({
    name: 'John Doe',
    phone: '123-456-7890',
    specialty: 'Software Engineering',
    email: 'john.doe@example.com',
    password: 'password123'
  });
  const [reservations, setReservations] = useState([
    // This should be fetched from a backend service
    { id: 1, eventName: 'Tech Conference', booked: true },
    { id: 2, eventName: 'Music Festival', booked: false }
  ]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const handlePasswordChange = (newPassword) => {
    setUserData({ ...userData, password: newPassword });
  };

  const cancelReservation = (reservationId, password) => {
    if (userData.password === password) {
      setReservations(
        reservations.map((reservation) =>
          reservation.id === reservationId ? { ...reservation, booked: false } : reservation
        )
      );
    } else {
      alert('Incorrect password');
    }
  };

  return (
    <>
    <NavbarPage></NavbarPage>
    <div>
      <h2>Personal Information</h2>
      <input
        type="text"
        name="name"
        value={userData.name}
        onChange={handleInputChange}
      />
      <input
        type="tel"
        name="phone"
        value={userData.phone}
        onChange={handleInputChange}
      />
      <input
        type="text"
        name="specialty"
        value={userData.specialty}
        onChange={handleInputChange}
      />
      <input
        type="email"
        name="email"
        value={userData.email}
        onChange={handleInputChange}
      />
      <button onClick={() => handlePasswordChange('newPassword')}>
        Change Password
      </button>

      <h2>Reservations</h2>
      {reservations.length > 0 ? (
        reservations.map((reservation) => (
          <div key={reservation.id}>
            <span>{reservation.eventName}</span>
            {reservation.booked ? (
              <button onClick={() => cancelReservation(reservation.id, userData.password)}>
                Cancel Reservation
              </button>
            ) : (
              <span>No reservations</span>
            )}
          </div>
        ))
      ) : (
        <p>No reservations have been made.</p>
      )}
    </div>
    </>
  );
};

export default PersonalPage;
