import React, { useState } from "react";
import { Link } from "react-router-dom";

const EventCard = ({ event }) => {
  const { photo, title, description, date, hour, address } = event;

  const handleBooking = () => {
    const password = prompt('Please enter your password to confirm booking:');
    // You can perform the booking confirmation logic here
    console.log('Password entered:', password);
  };

  return (
    <div className="max-w-xs mx-auto bg-white shadow-lg rounded-lg overflow-hidden m-4">
      <img className="w-full" src={photo} alt={title} />
      <div className="p-4">
        <h2 className="font-bold text-xl">{title}</h2>
        <p className="text-gray-600">{description}</p>
        <p>Date: {date}</p>
        <p>Hour: {hour}</p>
        <p>Address: {address}</p>
        <button onClick={handleBooking} className="mt-4 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
          Book
        </button>
      </div>
    </div>
  );
};
const EventList = ({ events }) => {
  return (
    <div className="flex flex-wrap justify-center">
      {events.map((event, index) => (
        <EventCard key={index} event={event} />
      ))}
    </div>
  );
};
const Home = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const events = [
    {
      id: "1",
      title: "College of Science Conference",
      description: "تتشرف وحدة الدعم الطلابي بكلية العلوم - جامعة قناة السويس - بدعوة سيادتكم لحضور فعاليات مؤتمر كلية العلوم السنوي السابع للأبحاث الطلابية ٢٠٢٤ تحت عنوان : «التحديات والإبداع: دور الذكاء الاصطناعي في تطوير العلوم»",
      imageUrl: require("./img/399994743_786024586870490_5833074945972968829_n-1.jpg"),
      date: "17/4/2024",
      hour: "10: AM",
      address: "Faculty of Science, Hall 1, Library Building - Ismailia"
    },
    {
      id: "2",
      title: "Meeting to professors",
      description: "تتشرف وحدة الدعم الطلابي بكلية العلوم - جامعة قناة السويس - بدعوة سيادتكم لحضور فعاليات مؤتمر كلية العلوم السنوي السابع للأبحاث الطلابية ٢٠٢٤ تحت عنوان : «التحديات والإبداع: دور الذكاء الاصطناعي في تطوير العلوم»",
      imageUrl: require("./img/1.jpg"),
      date: "17/4/2024",
      hour: "10: AM",
      address: "Faculty of Science, Hall 1, Library Building "
    },
    {
      id: "3",
      title: "party",
      description: "تتشرف وحدة الدعم الطلابي بكلية العلوم - جامعة قناة السويس - بدعوة سيادتكم لحضور فعاليات مؤتمر كلية العلوم السنوي السابع للأبحاث الطلابية ٢٠٢٤ تحت عنوان : «التحديات والإبداع: دور الذكاء الاصطناعي في تطوير العلوم»",
      imageUrl: require("./img/3.jpg"),
      date: "17/4/2024",
      hour: "10: AM",
      address: "Faculty of Science, Hall 1, Library Building "
    },
    {
      id: "3",
      title: "party",
      description: "تتشرف وحدة الدعم الطلابي بكلية العلوم - جامعة قناة السويس - بدعوة سيادتكم لحضور فعاليات مؤتمر كلية العلوم السنوي السابع للأبحاث الطلابية ٢٠٢٤ تحت عنوان : «التحديات والإبداع: دور الذكاء الاصطناعي في تطوير العلوم»",
      imageUrl: require("./img/3.jpg"),
      date: "17/4/2024",
      hour: "10: AM",
      address: "Faculty of Science, Hall 1, Library Building "
    }
  ];

  return (
    <>
      <nav className="bg-gray-900 md:flex flex-col md:flex-row md:space-x-2 p-4 space-x-0 md:space-y-0 fixed w-full top-0 z-10  hover:bg-white hover:border-b-2 border-black">
        <div className="container mx-auto flex flex-col md:flex-row md:justify-between md:items-center">
          <div className="flex justify-between items-center w-full md:w-auto">
            <Link to="/" className="text-yellow-500 text-4xl font-bold md:mb-0 mb-4 md:mb-0 hover:text-black">Even<span className="text-red-600">D</span>ango</Link>
            <button
              onClick={toggleDropdown}
              className="md:hidden text-yellow-500 hover:text-black focus:outline-none"
            >
              ☰
            </button>
          </div>
          <ul className={`md:flex flex-col md:flex-row md:space-x-2 p-4 space-x-0 md:space-y-0 ${isOpen ? 'block' : 'hidden'}`}>
            <li><Link to="/home" className="text-yellow-600 hover:text-black m-2 font-blond">Home</Link></li>
            <li><Link to="/profile" className="text-yellow-600 hover:text-black m-2 font-blond">Profile</Link></li>
            <li><Link to="/about" className="text-yellow-600 hover:text-black m-2 font-blond">About</Link></li>
            <li><Link to="/contact" className="text-yellow-600 hover:text-black m-2 font-blond">Contact</Link></li>
          </ul>
        </div>
      </nav>

      <div className="font-bold pl-8 mt-32 flex items-center text-blue-950 flex ">
        <div>All Events </div>
        <div className="relative ml-4 ml-20">
          <input
            type="text"
            placeholder="Search events" 
            className="border border-gray-300 rounded px-4 py-2 pl-10"
            value={searchTerm}
            onChange={handleSearch}
          />
          <span className="absolute inset-y-0 left-0 flex items-center pl-3">
            <svg
              className="h-5 w-5 text-gray-500"
              fill="none"
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"></path>
            </svg>
          </span>
        </div>
      </div>

      <div className="container mx-auto">
      <h1 className="text-3xl font-bold text-center my-8">Upcoming Events</h1>
      <EventList events={events} />
    </div>
            
        
    
    </>
  );
};

export default Home;
