import React, { useState } from 'react';

const CreateEventPage = () => {
  const [event, setEvent] = useState({
    id: '',
    title: '',
    description: '',
    address: '',
    date: '',
    hour: '',
    photo: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEvent(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Event Created:', event);
    // Here you can handle the event creation, e.g., send the data to a server
  };

  return (
    <div className="container">
      <form onSubmit={handleSubmit}>
        <label>
          ID:
          <input
            type="text"
            name="id"
            value={event.id}
            onChange={handleChange}
          />
        </label>
        <label>
          Title:
          <input
            type="text"
            name="title"
            value={event.title}
            onChange={handleChange}
          />
        </label>
        <label>
          Description:
          <textarea
            name="description"
            value={event.description}
            onChange={handleChange}
          />
        </label>
        <label>
          Address:
          <input
            type="text"
            name="address"
            value={event.address}
            onChange={handleChange}
          />
        </label>
        <label>
          Date:
          <input
            type="date"
            name="date"
            value={event.date}
            onChange={handleChange}
          />
        </label>
        <label>
          Hour:
          <input
            type="time"
            name="hour"
            value={event.hour}
            onChange={handleChange}
          />
        </label>
        <label>
          Photo URL:
          <input
            type="text"
            name="photo"
            value={event.photo}
            onChange={handleChange}
          />
        </label>
        <button type="submit">Create Event</button>
      </form>
    </div>
  );
};

export default CreateEventPage;
