import React, { useState } from 'react';

const InputPage = () => {
  const [inputValue, setInputValue] = useState('');

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Input Submitted: ${inputValue}`);
    // Here you can also handle the submission, e.g., send the data to a server
  };

  return (
    <div className="container">
      <form onSubmit={handleSubmit}>
        <label htmlFor="inputField">Enter your input:</label>
        <input
          type="text"
          id="inputField"
          value={inputValue}
          onChange={handleInputChange}
          className="input-class" // Add your CSS class for styling
        />
        <button type="submit" className="submit-button">Submit</button>
      </form>
    </div>
  );
};

export default InputPage;
