import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect, Link } from 'react-router-dom';
import EventDetail from './EventDetail';
import EventCard from './Home/component/EventCard';
import NavbarPage from './Home/navbar/navbar';
import Footer from './Home/navbar/Footer';

// Form component to create a new event
const CreateEventForm = ({ addEvent }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    photo: '',
    address: '',
    organizer: '',
    attendees: '',
    date: '',
    hour: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addEvent(formData);
    setFormData({
      title: '',
      description: '',
      photo: '',
      address: '',
      organizer: '',
      attendees: '',
      date: '',
      hour: ''
    }); // Reset form data
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Create New Event</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Add form fields for each property in formData */}
        {/* ... */}
        <button
          type="submit"
          className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          Create Event
        </button>
      </form>
    </div>
  );
};

// HomePage component to display events
const HomePage = ({ events }) => {
  return (
    <>
      <NavbarPage />
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 p-4">
        {events.map((event) => (
          <EventCard key={event.id} event={event} />
        ))}
      </div>
      <Footer />
    </>
  );
};

// Main App component
const App = () => {
  const [events, setEvents] = useState([]);
  const [redirect, setRedirect] = useState(false);

  const addEvent = (newEvent) => {
    newEvent.id = events.length + 1; // Simple ID assignment
    setEvents([...events, newEvent]);
    setRedirect(true); // Trigger redirect to homepage
  };

 
};

export default App;
