import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import myPhoto from './img/3.jpg'; // Import your photo file
import Logo from './img/Logo.png';

const LoginPage = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (formData.email === 'Evendango@gmail.com' && formData.password === '123456') {
      window.location.href = '/home';
    } else {
      setError('Email or password is incorrect');
    }
  };

  return (
    <>
     <nav className=" md:flex flex-col md:flex-row md:space-x-2 p-2 space-x-0 md:space-y-0 fixed w-full top-0 z-10  bg-gray-200">
        <div className="container mx-auto flex flex-col md:flex-row md:justify-between md:items-center">
          <div className="flex justify-between items-center w-full md:w-auto ">
          <img className="object-cover " src={Logo} alt="Profile" />
          
          </div>
        </div>
      </nav>
     
      <div className="min-h-screen flex items-center justify-center sm:py-12 mt-20 ">
  <div className="w-full p-6 bg-white rounded-lg shadow-md mx-auto flex items-center justify-center space-x-8">
    <div className=" bg-white">
      <div className="absolute inset-0 "></div>
    </div>
    <div className="max-w-md w-full">
      <div className="mb-8">
        <Link to="/login" className="text-red-400 text-2xl font-extrabold md:mb-0 mb-4">Even<span className="text-yellow-500">D</span>ango</Link>
        <p className="text-gray-600 flex justify-end">
          New User?{' '}
          <Link className="text-green-800 hover:underline pl-4" to="/register">
            Sign Up
          </Link>
        </p>
      </div>
      <div>
        <h2 className="mt-6 text-center text-3xl font-extrabold text-yellow-500">
          Welcome !
        </h2>
        <p className="mt-2 text-center text-sm text-gray-600 font-semibold">
          Login to continue
        </p>
      </div>
     
    </div>
  </div>
</div>

    </>
  );
};

export default LoginPage;
