import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const ProfilePage = () => {
  const [userData, setUserData] = useState({});
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    // استدعاء البيانات من الخادم
    fetchUserData();
    fetchBookings();
  }, []);

  const fetchUserData = async () => {
    try {
      const response = await fetch('/api/user-data'); // استبدل بمسار الطلب الفعلي
      const data = await response.json();
      setUserData(data);
    } catch (error) {
      console.error('Failed to fetch user data', error);
    }
  };

  const fetchBookings = async () => {
    try {
      const response = await fetch('/api/bookings'); // استبدل بمسار الطلب الفعلي
      const data = await response.json();
      setBookings(data);
    } catch (error) {
      console.error('Failed to fetch bookings', error);
    }
  };

  return (
    <>
      <nav>
        {/* تضمين رابط للعودة إلى صفحة الرئيسية أو أي صفحة أخرى */}
        <Link to="/">العودة إلى الصفحة الرئيسية</Link>
      </nav>
      <div>
        {/* عرض بيانات المستخدم */}
        <h2>بيانات المستخدم</h2>
        <p>الاسم: {userData.firstName} {userData.lastName}</p>
        <p>البريد الإلكتروني: {userData.email}</p>
        <p>الكلية: {userData.university}</p>
        <p>المستوى الأكاديمي: {userData.academicLevel}</p>
      </div>
      <div>
        {/* عرض قائمة الحجوزات */}
        <h2>الحجوزات</h2>
        <ul>
          {bookings.map((booking, index) => (
            <li key={index}>
              <p>اسم الحدث: {booking.eventName}</p>
              <p>تاريخ الحجز: {booking.bookingDate}</p>
              {/* إضافة المزيد من التفاصيل حسب حاجتك */}
            </li>
          ))}
        </ul>
      </div>
    </>
  );
};

export default ProfilePage;
