import React, { useState } from "react";
import { Link } from "react-router-dom";
const Card = ({ id, title, description, imageUrl, date, hour, address }) => {
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [password, setPassword] = useState("");
  const [bookingConfirmed, setBookingConfirmed] = useState(false);

  const handleConfirm = () => {
    if (password === "123456") {
      console.log("Booking confirmed with password:", password);
      setBookingConfirmed(true);
      setShowConfirmation(false);
    } else {
      console.log("Incorrect password. Booking not confirmed.");
      // You can also display an error message here if you want
    }
  };

  return (
    <div className="group relative bg-gray-200 bg-opacity-75 shadow-lg rounded-lg overflow-hidden">
      <div className="relative">
        <img
          className="h-96 w-full object-cover transition-transform transform group-hover:scale-105"
          src={imageUrl}
          alt={title}
        />
        {bookingConfirmed && (
          <div className="absolute inset-0 flex items-center justify-center bg-gray-800 bg-opacity-90">
            <p className="text-green-500 font-bold">Booking confirmed!</p>
            
          </div>
        )}
        <div className="absolute inset-0 bg-black bg-opacity-90 flex flex-col justify-center items-center opacity-0 group-hover:opacity-100 transition-opacity">
          <div className="md:text-white px-6 py-4 ">
            <p className="text-yellow-600 text-base font-bold md:m-4 pt-10">{description}</p>
            <p className="text-yellow-500 text-sm mt-2 font-bold md:m-2">Date: {date}</p>
            <p className="text-yellow-500 text-sm font-bold md:m-2">Hour: {hour}</p>
            <p className="text-yellow-500 text-sm font-bold  md:m-2 ml-4 ">Address: {address}</p>
          </div>
        </div>
      </div>
      <div className="absolute top-0 left-0 w-full bg-white bg-opacity-90 px-6 py-4 text-blue-400 font-extrabold text-xl">
        {title}
      </div>
      <div className="flex justify-between items-end absolute inset-x-0 bottom-0 p-4">
        
          <>
          {!bookingConfirmed && (
  <button
    onClick={() => setShowConfirmation(true)}
    className="bg-gray-800 hover:bg-green-400 text-white font-bold py-2 px-4 rounded m-2"
  >
    Book
  </button>
  )}
            {showConfirmation && (
              <div className="absolute inset-0 flex items-center justify-center pb-24 bg-gray-800 bg-opacity-90">
                <div className="bg-white p-4 rounded-lg">
                  <p className="mb-2">Enter your password to confirm booking:</p>
                  <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="border border-gray-300 rounded px-2 py-1"
                  />
                  <div className="mt-2 flex justify-end">
                    <button
                      onClick={handleConfirm}
                      className="bg-green-500 hover:bg-green-600 text-white font-bold py-1 px-2 rounded"
                    >
                      Confirm
                    </button>
                    <button
                      onClick={() => setShowConfirmation(false)}
                      className="ml-2 bg-red-500 hover:bg-red-600 text-white font-bold py-1 px-2 rounded"
                    >
                      Cancel
                    </button>
                  </div>
                </div>
              </div>
            )}
          </>
     
      </div>
    </div>
  );
};

const Home = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const events = [
    {
      id: "1",
      title: "College of Science Conference",
      description: "تتشرف وحدة الدعم الطلابي بكلية العلوم - جامعة قناة السويس - بدعوة سيادتكم لحضور فعاليات مؤتمر كلية العلوم السنوي السابع للأبحاث الطلابية ٢٠٢٤ تحت عنوان : «التحديات والإبداع: دور الذكاء الاصطناعي في تطوير العلوم»",
      imageUrl: require("./img/399994743_786024586870490_5833074945972968829_n-1.jpg"),
      date: "17/4/2024",
      hour: "10: AM",
      address: "Faculty of Science, Hall 1, Library Building - Ismailia"
    },
    {
      id: "2",
      title: "hassan",
      description: "تتشرف وحدة الدعم الطلابي بكلية العلوم - جامعة قناة السويس - بدعوة سيادتكم لحضور فعاليات مؤتمر كلية العلوم السنوي السابع للأبحاث الطلابية ٢٠٢٤ تحت عنوان : «التحديات والإبداع: دور الذكاء الاصطناعي في تطوير العلوم»",
      imageUrl: require("./img/1.jpg"),
      date: "17/4/2024",
      hour: "10: AM",
      address: "Faculty of Science, Hall 1, Library Building "
    },
    {
      id: "3",
      title: "party",
      description: "تتشرف وحدة الدعم الطلابي بكلية العلوم - جامعة قناة السويس - بدعوة سيادتكم لحضور فعاليات مؤتمر كلية العلوم السنوي السابع للأبحاث الطلابية ٢٠٢٤ تحت عنوان : «التحديات والإبداع: دور الذكاء الاصطناعي في تطوير العلوم»",
      imageUrl: require("./img/3.jpg"),
      date: "17/4/2024",
      hour: "10: AM",
      address: "Faculty of Science, Hall 1, Library Building "
    }
  ];

  return (
    <>
    <nav className="bg-gray-900 md:flex flex-col md:flex-row md:space-x-2 p-4 space-x-0 md:space-y-0 fixed w-full top-0 z-10  hover:bg-white hover:border-b-2 border-black">
  <div className="container mx-auto flex flex-col md:flex-row md:justify-between md:items-center">
    <div className="flex justify-between items-center w-full md:w-auto">
      <Link to="/" className="text-yellow-500 text-4xl font-bold md:mb-0 mb-4 md:mb-0 hover:text-black">Even<span className="text-red-600">D</span>ango</Link>
      <button
        onClick={toggleDropdown}
        className="md:hidden text-white hover:text-yellow-500 focus:outline-none"
      >
        ☰
      </button>
    </div>
    <ul className={`md:flex flex-col md:flex-row md:space-x-2 p-4 space-x-0 md:space-y-0 ${isOpen ? 'block' : 'hidden'}`}>
      <li><Link to="/home" className="text-yellow-600 hover:text-black m-2 font-blond">Home</Link></li>
      <li><Link to="/profile" className="text-yellow-600 hover:text-black m-2 font-blond">Profile</Link></li>
      <li><Link to="/about" className="text-yellow-600 hover:text-black m-2 font-blond">About</Link></li>
      <li><Link to="/contact" className="text-yellow-600 hover:text-black m-2 font-blond">Contact</Link></li>
    </ul>
    <div className="md:container mx-auto w-full md:w-2/3 bg-blue-100 p-4 mt-">
      <i class='bx bx-search'></i>
        <input
          type="text"
          placeholder="Search events"
          className="border border-gray-300 rounded px-4 py-2 mt-4"
          value={searchTerm}
          onChange={handleSearch}
        />
  </div>
  </div>
</nav>


<div className="font-bold pl-8 mt-32  text-blue-950">
        All Events Available
      </div>
     
      

        <div className="flex flex-col md:flex-row md:flex-wrap md:-mx-2">
          {events
            .filter((event) =>
              event.title.toLowerCase().includes(searchTerm.toLowerCase())
            )
            .map((event) => (
              <div key={event.id} className="w-full md:w-1/2 p-2">
                <Card
                  id={event.id}
                  title={event.title}
                  description={event.description}
                  imageUrl={event.imageUrl}
                  date={event.date}
                  hour={event.hour}
                  address={event.address}
                />
              </div>
            ))}
