import React, { useState } from 'react';

const RegistrationPage = () => {
  const [userType, setUserType] = useState('user'); // الافتراضي هو 'user'

  const handleUserTypeChange = (event) => {
    setUserType(event.target.value);
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded shadow-md w-full max-w-md">
        <h2 className="text-lg mb-4">Register as :</h2>
        <select
          className="block w-full p-2 border rounded mb-4"
          onChange={handleUserTypeChange}
          value={userType}
        >
          <option value="user">User</option>
          <option value="admin">Manger</option>
        </select>

        {userType === 'user' ? (
          <UserRegistrationForm />
        ) : (
          <AdminRegistrationForm />
        )}
      </div>
    </div>
  );
};

const UserRegistrationForm = () => {
  return (
    <form className="space-y-4">
      <h3 className="text-md">ٍRegister as a user</h3>
      {/* أضف حقول النموذج الخاصة بك هنا */}
      <button
        type="submit"
        className="w-full bg-blue-500 text-white p-2 rounded"
      >
        submit
      </button>
    </form>
  );
};

const AdminRegistrationForm = () => {
  // You can add state and handle changes for each field as needed
  return (
    <form className="space-y-4">
      <h3 className="text-md">Administrator Registration</h3>
      <div>
        <label htmlFor="name" className="block text-sm font-medium text-gray-700">
          Name
        </label>
        <input
          type="text"
          id="name"
          className="mt-1 block w-full p-2 border border-gray-300 rounded shadow-sm"
          placeholder="Enter your name"
          required
        />
      </div>
      <div>
        <label htmlFor="email" className="block text-sm font-medium text-gray-700">
          Email
        </label>
        <input
          type="email"
          id="email"
          className="mt-1 block w-full p-2 border border-gray-300 rounded shadow-sm"
          placeholder="example@example.com"
          required
        />
      </div>
      <div>
        <label htmlFor="password" className="block text-sm font-medium text-gray-700">
          Password
        </label>
        <input
          type="password"
          id="password"
          className="mt-1 block w-full p-2 border border-gray-300 rounded shadow-sm"
          required
        />
      </div>
      <div>
        <label htmlFor="confirm-password" className="block text-sm font-medium text-gray-700">
          Confirm Password
        </label>
        <input
          type="password"
          id="confirm-password"
          className="mt-1 block w-full p-2 border border-gray-300 rounded shadow-sm"
          required
        />
      </div>
      <button
        type="submit"
        className="w-full bg-green-500 text-white p-2 rounded"
      >
        Register as Administrator
      </button>
    </form>
  );
};


export default RegistrationPage;
