import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import myPhoto from './img/3.jpg'; // Import your photo file
import Logo from './img/Logo.png';



const teamMembers = [
  {
    id: 1,
    name: "Youssef Mohamed",
    role: "CTO",
    photo: {Logo},
    description: "Back-End",
    social: {
      facebook: "https://facebook.com/flepoooo",
      linkedin: "https://www.linkedin.com/in/flepooo",
      github: "https://github.com/flepooo",
    },
  },
  {
    id: 2,
    name: "Eslam Eid",
    role: "CEO",
    photo: "/team/E.jpg",
    description: "Head of Front-End",
    social: {
      facebook: "https://facebook.com/flepoooo",
      linkedin: "https://www.linkedin.com/in/flepooo",
      github: "https://github.com/flepooo",
    },
  },
  {
    id: 3,
    name: "Basem Mohamed",
    role: "Dev",
    photo: "/team/B.jpeg",
    description: "Front-End",
    social: {
      facebook: "https://facebook.com/flepoooo",
      linkedin: "https://www.linkedin.com/in/flepooo",
      github: "https://github.com/flepooo",
    },
  },
  {
    id: 4,
    name: "Ahmed Salah",
    role: "Dev",
    photo: "/team/A.jpeg",
    description: "Front-End",
    social: {
      facebook: "https://facebook.com/flepoooo",
      linkedin: "https://www.linkedin.com/in/flepooo",
      github: "https://github.com/flepooo",
    },
  },
  // Add more team members here
];
const About = () => {
  return (
    <main className="p-4 md:p-10 mx-auto max-w-7xl">
      <div className="container mx-auto py-8">
        <h1 className="text-4xl font-bold mb-4">Our Amazing Team</h1>
        <p className="text-lg mb-8">
          Welcome to our company! We are a team of passionate individuals dedicated to making the world a better place through technology.
        </p>
        <h2 className="text-2xl font-bold mb-4">Meet the Team</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4">
          {teamMembers.map((member) => (
            <div key={member.id} className="border p-4 rounded-lg">
              <div className="flex justify-center">
                <img
                  src={member.photo}
                  alt={member.name}
                  className="rounded-full"
                />
              </div>
              <div className="text-center mt-4">
                <h3 className="text-xl font-bold">{member.name}</h3>
                <p className="text-gray-600 mb-2">{member.role}</p>
                <p>{member.description}</p>
                <div className="flex justify-center mt-4">
                  <a
                    href={member.social.github}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="mr-4"
                  >
                    <img
                      src="/github-icon.png"
                      alt="GitHub"
                      style={{ width: 30, height: 30 }}
                    />
                  </a>
                  <a
                    href={member.social.linkedin}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="mr-4"
                  >
                    <img
                      src="/linkedin-icon.png"
                      alt="LinkedIn"
                      style={{ width: 30, height: 30 }}
                    />
                  </a>
                  <a
                    href={member.social.facebook}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="mr-4"
                  >
                    <img
                      src="/facebook-icon.png"
                      alt="Facebook"
                      style={{ width: 30, height: 30 }}
                    />
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
      <p className="text-lg font-medium my-3">
        Â© 2024 Your Company. All Rights Reserved.
      </p>
      <p>Designed by Your Development Team</p>
    </main>
  );
};

export default About;
