import React, { useState } from 'react';

const InvitePage = () => {
  const [emails, setEmails] = useState('');
  const [excelFile, setExcelFile] = useState(null);

  const handleEmailsChange = (e) => {
    setEmails(e.target.value);
  };

  const handleExcelFileChange = (e) => {
    const file = e.target.files[0];
    setExcelFile(file);
  };

  const handleSendInvitations = () => {
    // Logic for sending invitations
    console.log('Sending invitations:', emails);
  };

  const handleUploadExcel = () => {
    // Logic for uploading Excel file
    console.log('Uploading Excel file:', excelFile);
  };

  return (
    <div className="mx-auto w-3/4">
      <h1 className="text-2xl font-bold mb-4">Send Invitations</h1>
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="emails">
          Enter Emails (separated by commas)
        </label>
        <input
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          id="emails"
          type="text"
          placeholder="Emails"
          value={emails}
          onChange={handleEmailsChange}
        />
      </div>
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="excel">
          Upload Excel File
        </label>
        <input
          className="hidden"
          id="excel"
          type="file"
          accept=".xls,.xlsx"
          onChange={handleExcelFileChange}
        />
        <label
          htmlFor="excel"
          className="cursor-pointer bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        >
          Choose Excel File
        </label>
        {excelFile && <span className="ml-2">{excelFile.name}</span>}
      </div>
      <div className="mb-4">
        <button
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          onClick={handleSendInvitations}
        >
          Send Invitations
        </button>
        <button
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline ml-2"
          onClick={handleUploadExcel}
        >
          Upload Excel
        </button>
      </div>
    </div>
  );
};

export default InvitePage;
