import React, { useState } from 'react';

const InvitePage = () => {
  const [emails, setEmails] = useState('');
  const [excelFile, setExcelFile] = useState(null);
  const [selectedEvent, setSelectedEvent] = useState('');

  const handleEmailsChange = (e) => {
    setEmails(e.target.value);
  };

  const handleExcelFileChange = (e) => {
    const file = e.target.files[0];
    setExcelFile(file);
  };

  const handleSendInvitations = () => {
    // Logic for sending invitations
    console.log('Sending invitations:', emails, 'to event with ID:', selectedEvent);
  };

  const handleUploadExcel = () => {
    // Logic for uploading Excel file
    console.log('Uploading Excel file:', excelFile, 'to event with ID:', selectedEvent);
  };

  const handleEventChange = (e) => {
    setSelectedEvent(e.target.value);
  };

  return (
    <>
     <nav className="bg-gray-800 py-4">
      <div className="container mx-auto flex justify-between items-center">
        <div className="text-white font-bold ">Even <span className="text-yellow-500">D</span>ango</div>
        <ul className="flex space-x-4">
          <li><a href="/home" className="text-white  hover:text-yellow-500">Home</a></li>
          <li><a href="/event" className="text-white  hover:text-yellow-500">Creat</a></li>
          <li><a href="/invit" className="text-white  hover:text-yellow-500">Invitations</a></li>
          <li><a href="/home" className="text-white  hover:text-yellow-500">profile</a></li>
          <li><a href="/about" className="text-white  hover:text-yellow-500">About</a></li>
          <li><a href="#" className="text-white  hover:text-yellow-500">Contact</a></li>
        </ul>
      </div>
    </nav>
    <div className="mx-auto w-3/4 m-16">
      <h1 className="text-2xl font-bold mb-4">Send Invitations</h1>
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="emails">
          Enter Emails (separated by commas)
        </label>
        <input
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          id="emails"
          type="text"
          placeholder="Emails"
          value={emails}
          onChange={handleEmailsChange}

        />
          <button
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline m-4"
          onClick={handleSendInvitations}
        >
          Send Invitation
        </button>
      </div>
      <div className="m-4">
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-8" htmlFor="excel">
        Enter an Excel file containing emails to send direct invitations to them        </label>
        <input
          className="hidden"
          id="excel"
          type="file"
          accept=".xls,.xlsx"
          onChange={handleExcelFileChange}
        />
        <label
          htmlFor="excel"
          className="cursor-pointer bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline mb-8 "
        >
          Choose Excel File
        </label>
        {excelFile && <span className="ml-2">{excelFile.name}</span>}
      </div>
      
        <button
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline mt-4"
          onClick={handleUploadExcel}
        >
          Upload Excel
        </button>
      </div>
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="eventSelect">
          Select Event
        </label>
        <select
          id="eventSelect"
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          value={selectedEvent}
          onChange={handleEventChange}
        >
          <option value="">Select an event</option>
          <option value="1">Event 1</option>
          <option value="2">Event 2</option>
          <option value="3">Event 3</option>
        </select>
      </div>
    </div>
    </>
  );
};

export default InvitePage;
