import React, { useState } from 'react';
import axios from 'axios';

const EventInvitation = () => {
  const [email, setEmail] = useState('');
  const [title, setTitle] = useState('');

  const sendInvitation = async () => {
    try {
      const eventDetails = {
        title, // Add default values for date and location if needed
        date: '2024-05-20', // Example default date
        location: 'Hurghada, Egypt' // Example default location
      };

      const response = await axios.post('/api/send-invitation', {
        email,
        eventDetails,
      });
      console.log(response.data);
      alert('Invitation sent successfully!');
    } catch (error) {
      console.error('Error sending invitation:', error);
      alert('Failed to send invitation.');
    }
  };

  return (
    <div>
      <h2>Send Event Invitation</h2>
      <input
        type="email"
        placeholder="Enter recipient's email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="text"
        name="title"
        placeholder="Event Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />
      <button onClick={sendInvitation}>Send Invitation</button>
    </div>
  );
};

export default EventInvitation;
