import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const RegistrationPage = () => {
  const [userType, setUserType] = useState('user'); // Default to 'user'

  const handleUserTypeChange = (event) => {
    setUserType(event.target.value);
  };

  return (
    <>
      <nav className="bg-gray-900 md:flex flex-col md:flex-row md:space-x-2 p-4 space-x-0 md:space-y-0 fixed w-full top-0 z-10 hover:bg-white hover:border-b-2 border-black">
        {/* Navigation content */}
      </nav>

      <div className="min-h-screen flex items-center justify-center bg-gray-100 py-12">
        <div className="max-w-md w-full bg-white p-8 rounded-lg shadow-md">
          <div className="mb-4">
            <h2 className="text-center text-2xl font-extrabold">Register as:</h2>
            {/* Rest of your registration form */}
          </div>
        </div>
      </div>
    </>
  );
};

// UserRegistrationForm and AdminRegistrationForm components remain the same

export default RegistrationPage;
