import React, { useState } from 'react';
import axios from 'axios';

const RegistrationForm = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    first_name: '',
    last_name: '',
    phone_number: '',
    otp: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/users', formData);
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>User Registration</h2>
      <form onSubmit={handleSubmit}>
        <label>Username:</label>
        <input type="text" name="username" value={formData.username} onChange={handleChange} required /><br /><br />
        <label>Email:</label>
        <input type="email" name="email" value={formData.email} onChange={handleChange} required /><br /><br />
        <label>Password:</label>
        <input type="password" name="password" value={formData.password} onChange={handleChange} required /><br /><br />
        <label>First Name:</label>
        <input type="text" name="first_name" value={formData.first_name} onChange={handleChange} required /><br /><br />
        <label>Last Name:</label>
        <input type="text" name="last_name" value={formData.last_name} onChange={handleChange} /><br /><br />
        <label>Phone Number:</label>
        <input type="tel" name="phone_number" value={formData.phone_number} onChange={handleChange} /><br /><br />
        <label>OTP:</label>
        <input type="text" name="otp" value={formData.otp} onChange={handleChange} /><br /><br />
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default RegistrationForm;
