import { useRouter } from "next/router";
import React, { useState } from "react";
import { Title, Button } from "@tremor/react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Cookies from "js-cookie";

const Login = ({ access_token }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const router = useRouter();

  const handleLogin = async () => {
    const id = toast.loading("Please wait...", {
      closeButton: true,
    });
    const response = await fetch("/api/login", {
      method: "POST",
      body: new URLSearchParams({
        UserName: email,
        Password: password,
      }),
    });

    const result = await response.json();
    if (response.ok) {
      const data = JSON.parse(result);
      Cookies.set("username", data.userName, { expires: 14 });
      Cookies.set("Role", data.Role, { expires: 14 });
      Cookies.set("UserId", data.UserId, { expires: 14 });
      Cookies.set("access_token", data.access_token, { expires: 14 });
      await router.push("/");
    } else {
      toast.update(id, {
        render: "Wrong email or password",
        type: "error",
        isLoading: false,
        closeButton: true,
      });
    }
  };

  return (
    <main className="p-4 md:p-10 mx-auto max-w-7xl">
      <ToastContainer />
      <Title>
        <img src={"/logo.png"} width={40} />
        CDMS
      </Title>
      <div className="bg-white p-10 rounded-lg">
        <h1 className="text-2xl font-medium mb-6">Login</h1>
        <div className="mb-4">
          <label className="block text-gray-700 font-bold mb-2" htmlFor="email">
            Email
          </label>
          <input
            className="border rounded-lg px-3 py-2 w-full"
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="mb-6">
          <label
            className="block text-gray-700 font-bold mb-2"
            htmlFor="password"
          >
            Password
          </label>
          <input
            className="border rounded-lg px-3 py-2 w-full"
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <Button color="black" size="md" onClick={handleLogin}>
          Login
        </Button>
      </div>
    </main>
  );
};

export async function getServerSideProps({ req }) {
  const access_token = req.cookies.access_token
    ? req.cookies.access_token
    : null;

  if (access_token) {
    return {
      redirect: {
        destination: "/",
        permanent: false,
      },
    };
  }
  return {
    props: { access_token },
  };
}

export default Login;
