import React, { useState } from 'react';
import emailjs from 'emailjs-com';

const EmailForm = () => {
  const [emailData, setEmailData] = useState({
    recipientEmail: '',
    eventTitle: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEmailData({
      ...emailData,
      [name]: value
    });
  };

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.send('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', emailData, 'YOUR_USER_ID')
      .then((response) => {
        console.log('SUCCESS!', response.status, response.text);
      }, (err) => {
        console.log('FAILED...', err);
      });
  };

  return (
    <form onSubmit={sendEmail}>
      <input
        type="email"
        name="recipientEmail"
        placeholder="Recipient's email"
        value={emailData.recipientEmail}
        onChange={handleChange}
      />
      <select name="eventTitle" value={emailData.eventTitle} onChange={handleChange}>
        <option value="">Select Event Title</option>
        <option value="Birthday">Birthday</option>
        <option value="Anniversary">Anniversary</option>
        <option value="Meeting">Meeting</option>
        // Add more event titles as needed
      </select>
      <button type="submit">Send Email</button>
    </form>
  );
};

export default EmailForm;
