import React, { useState } from 'react';
import NavbarPage from './Home/navbar/navbar';
const PersonalPage = () => {
  const [userData, setUserData] = useState({
    name: 'John Doe',
    phone: '123-456-7890',
    specialty: 'Software Engineering',
    email: 'john.doe@example.com',
    password: 'password123'
  });
  const [reservations, setReservations] = useState([
    // This should be fetched from a backend service
    
  ]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const handlePasswordChange = (newPassword) => {
    setUserData({ ...userData, password: newPassword });
  };

  const cancelReservation = (reservationId, password) => {
    if (userData.password === password) {
      setReservations(
        reservations.map((reservation) =>
          reservation.id === reservationId ? { ...reservation, booked: false } : reservation
        )
      );
    } else {
      alert('Incorrect password');
    }
  };

  return(<div className="container mx-auto p-4">
  <h2 className="text-2xl font-bold mb-4">Personal Information</h2>
  <div className="mb-4">
    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="name">
      Name
    </label>
    <input
      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
      id="name"
      type="text"
      placeholder="Enter your name"
      name="name"
      value={userData.name}
      onChange={handleInputChange}
    />
  </div>
  {/* Repeat for other fields... */}
  
  <h2 className="text-2xl font-bold mt-8 mb-4">Reservations</h2>
  <div className="space-y-4">
    {reservations.length > 0 ? (
      reservations.map((reservation) => (
        <div key={reservation.id} className="flex justify-between items-center p-4 bg-gray-100 rounded">
          <span className="font-medium">{reservation.eventName}</span>
          {reservation.booked ? (
            <button
              className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              onClick={() => cancelReservation(reservation.id, userData.password)}
            >
              Cancel Reservation
            </button>
          ) : (
            <span>No reservations</span>
          )}
        </div>
      ))
    ) : (
      <p>No reservations have been made.</p>
    )}
  </div>
</div>
  );
};

export default PersonalPage;
